 		TagLib.Id3v2.Tag tag = file.GetTag (TagTypes.Id3v2, true) as TagLib.Id3v2.Tag;
 		if (tag == null)
 			throw new ApplicationException ("File does not support ID3v2 tags.");

 		// Create a picture. Can throw file related exceptions.
